sys_script.script:
(function executeRule(current, previous /*null when async*/) {

    gs.addInfoMessage("Business Rule executando para: " + current.name);

    if (current.sys_class_name == 'x_1851077_atvidade_supervisores') {
        var roleName = 'x_1851077_atvidade.supervisor';
        var userRole = new GlideRecord('sys_user_has_role');
        userRole.addQuery('user', current.sys_id);
        userRole.addQuery('role.name', roleName);
        userRole.query();

        if (!userRole.next()) {
            var newRole = new GlideRecord('sys_user_has_role');
            newRole.initialize();
            newRole.user = current.sys_id;
            newRole.role.setDisplayValue(roleName);
            newRole.insert();

            gs.addInfoMessage("✅ Role atribuída: " + roleName);
        } else {
            gs.addInfoMessage("ℹ️ Usuário já possui a role: " + roleName);
        }
    } else {
        gs.addInfoMessage("⚠️ sys_class_name diferente: " + current.sys_class_name);
    }

})(current, previous);
